function comment = OR_reporter(fit, chisq)% writes an output report for a% 31P MAS fit using final results from% OR_main.% saves (append) fit values % as text to file: reportname% in current directory %% SRK 18-02-2004OR_inputsOR_initftime = datestr(now,0); freq = freq/1e6;% change formats for ease of interpbetaAngle = fit(1)*(180/pi); mosaicSpread = fit(2)*(180/pi);fractionIsotropic = fit(3); lineWidth = 100*fit(4);fout = fopen(reportname,'a') % appends datafprintf(fout, 'Oriented sample MAS fit results %21s \n', ftime);fprintf(fout, 'Results in %-s \n', resultsfile); fprintf(fout, 'Input file: %-s \t', nmrdatafile); fprintf(fout, ' \n');fprintf(fout, '%s\n', notes );fprintf(fout, 'CSA: iso %3.2f ppm, \t delta %3.1f ppm, \t eta %1.1f \n', w_iso, delta, eta);fprintf(fout, 'Frequency: %3.3f MHz, \t spin rate: %5.0f Hz \n', freq, w_rot);fprintf(fout, ' \n');fprintf(fout, 'Orientation Angle: %3.2f degrees, \t Mosaic spread: %2.1f degrees \n', betaAngle, mosaicSpread);fprintf(fout, 'Fraction isotropic: %3.1f percent, \t linewidth: %4.2f Hz \n', fractionIsotropic, lineWidth);fprintf(fout, 'Final chi-squared: %1.4e \n', chisq);fprintf(fout, '\n'); fprintf(fout, '\n'); comment = 1;fclose(fout);eval(['save ' resultsfile ' nmrdatafile reportname notes']); eval(['save ' resultsfile ' ftime betaAngle mosaicSpread '...		'fractionIsotropic lineWidth -append']);