%OR_simbasis.m%% set simulations parameters for 31P oriented fitting%%global OR_mosaic_spread OR_angleglobal CrystalliteList BetaAngles angls% general simulation parameters:thetaM = atan(sqrt(2));N = 512; %time resolution as a fraction of the rotor period%isotropicavg_scheme = 'StepHemi256'; % Need 2-angle!!!!% oriented: calculate range and number of anglesnorient = 500;% set up lists:angls = linspace(0,2*pi,N);loadstring = ['load ' avg_scheme '.dat -ascii'];eval(loadstring);CrystalliteList=eval(avg_scheme);width = 5*2*OR_mosaic_spread; BetaAngles = linspace(OR_angle - width, OR_angle + width, norient);