function spect = stick2spect(sticks, n_pts, sw, w_rot, max, nangles)%% expands stick spectra to full spectral widthspect = zeros(1,n_pts);k = linspace(-nangles/2,(nangles/2)-1,nangles);spect_basis = k*w_rot; % Calculate sideband frequenciesplace = (n_pts/2)+round(n_pts*spect_basis/sw); %calculate indices for histogramplace = place(((nangles/2)-max):((nangles/2)+max)); % based on spectrum of 25 pts spect(place) = sticks;